import { useState } from "react";

function BugLogTable(props) {

    const [color, setColor] = useState(0);
    const { bugLogs } = props;

    const handleChangeColor = () => {
        setColor(color + 1);
    }

    let actualColor = "red";
    if (color % 3 === 0) {
        actualColor = "red";
    } else if (color % 3 === 1) {
        actualColor = "blue";
    } else {
        actualColor = "green";
    }

    return (
        <>
            <style>
                {`
                    table{
                    border:1px solid black;
                    border-collapse: collapse;
                    width: 90%
                    }
                    th, td{
                    border:1px solid black;
                    }
                `}
            </style>
            <table style={{ padding: "10px", margin: "5px" }}>
                <thead>
                    <tr>
                        <th>Id</th>
                        <th>Name</th>
                        <th>Date</th>
                        <th>Project</th>
                        <th>Description</th>
                        <th>Severity</th>
                        <th>Priority</th>
                        <th>Status</th>
                    </tr>
                </thead>
                <tbody>
                    {bugLogs.map((bugLog) => (
                        <tr key={bugLog.id}>
                            <td>{bugLog.id}</td>
                            <td>{bugLog.name}</td>
                            <td>{bugLog.date}</td>
                            <td>{bugLog.project}</td>
                            <td>{bugLog.description}</td>
                            <td>{bugLog.severity}</td>
                            <td>{bugLog.priority}</td>
                            <td>{bugLog.status}</td>
                        </tr>
                    ))}
                </tbody>
            </table>

            <div style={{ backgroundColor: actualColor }}>
                <img src='https://www-cs-faculty.stanford.edu/~knuth/don.gif' alt='Donald Knuth' />
                <div>
                    <button onClick={handleChangeColor}>Change Color</button>
                </div>

            </div>
        </>
    );
}

export default BugLogTable;
