import { useState } from "react";
import { Client } from "@notionhq/client"

function BugLogTable(props) {

    const [color, setColor] = useState(0);
    const { bugLogs } = props;

    // Notion
    const notion = new Client({ auth: 'secret_qUIDR4gsSTy9LKVOoHvlrlC6Q3yXX9QayWijPLBp0ei' })
    const databaseId = process.env.NOTION_DATABASE_ID

    (async () => {
        const response = await notion.databases.query({
            database_id: 'a05a66e769a04a9f85e10b0466bbc317'
        });
        console.log(response);
    })();


    const handleChangeColor = () => {
        setColor(color + 1);
    }

    let actualColor = "red";
    if (color % 3 === 0) {
        actualColor = "red";
    } else if (color % 3 === 1) {
        actualColor = "blue";
    } else {
        actualColor = "green";
    }

    return (
        <>
            <style>
                {`
                    table{
                        margin-left: auto;
                        margin-right: auto;
                        margin-bottom: 10px;
                        border:1px solid black;
                        border-collapse: collapse;
                        width: 90%;
                    }
                    th, td{
                        border:1px solid black;
                    }
                `}
            </style>
            <table>
                <thead>
                    <tr>
                        <th>Id</th>
                        <th>Name</th>
                        <th>Date</th>
                        <th>Project</th>
                        <th>Description</th>
                        <th>Severity</th>
                        <th>Priority</th>
                        <th>Status</th>
                    </tr>
                </thead>
                <tbody>
                    {bugLogs.map((bugLog) => (
                        <tr key={bugLog.id}>
                            <td>{bugLog.id}</td>
                            <td>{bugLog.name}</td>
                            <td>{bugLog.date}</td>
                            <td>{bugLog.project}</td>
                            <td>{bugLog.description}</td>
                            <td>{bugLog.severity}</td>
                            <td>{bugLog.priority}</td>
                            <td>{bugLog.status}</td>
                        </tr>
                    ))}
                </tbody>
            </table>

            <div style={{ backgroundColor: actualColor }}>
                <img src='https://www-cs-faculty.stanford.edu/~knuth/don.gif' alt='Donald Knuth' />
                <div>
                    <button onClick={handleChangeColor}>Change Color</button>
                </div>

            </div>
        </>
    );
}

export default BugLogTable;
